package pack_2;

public class OddEven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 10;
        if (num % 2 == 0) {
            System.out.println("Entered Number is Even");
        }
 
        else {
            System.out.println("Entered Number is Odd");

	}

}
}
